from langflow.custom import Component
from langflow.io import DataInput, MultilineInput, Output, StrInput
from typing import Any, List, Dict
from langflow.schema.message import Message

class ParseDataComponent(Component):
    display_name = "Parse Data"
    description = "Convert a list of dicts (data) into plain text using a template."
    icon = "braces"
    name = "ParseData"

    inputs = [
        DataInput(
            name="data",
            display_name="Data",
            info="The data to convert to text. Should be a list of dicts.",
            is_list=True,
        ),
        MultilineInput(
            name="template",
            display_name="Template",
            info="The template to use for formatting the data. Use {key} for keys in the data.",
            value="{text}",
        ),
        StrInput(name="sep", display_name="Separator", advanced=True, value="\n"),
    ]

    outputs = [
        Output(
            display_name="Text",
            name="text",
            info="Data as a single Message, with each input data separated by Separator",
            method="parse_data",
        ),
        Output(
            display_name="Data List",
            name="data_list",
            info="Data as a list of dicts, each having `text` formatted by Template",
            method="parse_data_as_list",
        ),
    ]

    def _clean_args(self) -> tuple[List[Dict[str, Any]], str, str]:
        data = self.data if isinstance(self.data, list) else [self.data]
        template = self.template
        sep = self.sep
        return data, template, sep

    def parse_data(self) -> Message:
        data, template, sep = self._clean_args()
        result_string = self.data_to_text(template, data, sep)
        self.status = result_string
        return Message(text=result_string)

    def parse_data_as_list(self) -> List[Dict[str, Any]]:
        data, template, _ = self._clean_args()
        text_list, data_list = self.data_to_text_list(template, data)
        for item, text in zip(data_list, text_list, strict=True):
            item["text"] = text
        self.status = data_list
        return data_list

    @staticmethod
    def data_to_text(template: str, data: List[Dict[str, Any]], sep: str) -> str:
        formatted = []
        for item in data:
            try:
                formatted.append(template.format(**item))
            except Exception:
                formatted.append(str(item))
        return sep.join(formatted)

    @staticmethod
    def data_to_text_list(template: str, data: List[Dict[str, Any]]):
        text_list = []
        data_list = []
        for item in data:
            try:
                text = template.format(**item)
            except Exception:
                text = str(item)
            text_list.append(text)
            data_list.append(dict(item))
        return text_list, data_list

from langflow.custom import Component
from langflow.io import DataInput, Output
from langflow.schema import Data
from loguru import logger


class EntityNormalisationExtraction(Component):
    display_name = "Entity Normalisation Extraction"
    description = "Identifies and extracts entities with medical codes."
    icon = "Autonomize"
    name = "EntityNormalizationExtraction"

    inputs = [
        DataInput(
            name="data",
            display_name="Data",
            info="Entity prediction data",
            required=True,
        ),
    ]

    outputs = [
        Output(
            display_name="Filtered Entities",
            name="filtered_entities",
            method="process_entities",
        ),
    ]

    def process_entities(self) -> Data:
        try:
            data = self.data.data
            if isinstance(data, dict) and "value" in data:
                data = data["value"]

            logger.debug(f"Processing data: {data}")
            output = []

            for entity in data:
                if (
                    entity.get("ICD10CMConcepts")
                    or entity.get("CPT_Current_Procedural_Terminology")
                    or entity.get("RxNormConcepts")
                ):
                    output.append(entity)

            logger.debug(f"Found {len(output)} entities with medical codes")
            return Data(value={"data": output})

        except Exception as e:
            logger.error(f"Error processing entities: {e!s}")
            raise ValueError(f"Error processing entities: {e!s}")
