            if input.name == "ignore_unsupported_extensions"
        ),
        next(
            input
            for input in BaseFileComponent._base_inputs
            if input.name == "ignore_unspecified_files"
        ),
    ]

    outputs = [
        Output(
            name="srfIdentification",
            display_name="SRF Identification",
            method="process_srf",
        ),
    ]

    # Explicitly define the endpoint
    def __init__(self, **kwargs):
        ATModelComponent.__init__(self, **kwargs)  # Initialize ATModelComponent first
        BaseFileComponent.__init__(self, **kwargs)  # Then initialize BaseFileComponent
        self._modelhub_service = None
        self.temp_dir = tempfile.mkdtemp()
        self._downloaded_files = {}
        self._endpoint = "https://tolstoy-v2.modelhub.sprint.autonomize.dev/v1/models/identification:predict"

    async def _download_file_from_url(self, url: str) -> str | None:
        """Download a file from a URL."""
        try:
            filename = os.path.basename(urlparse(url).path)
            if not filename:
                filename = "downloaded_image.png"

            local_path = os.path.join(self.temp_dir, filename)

            async with aiohttp.ClientSession() as session:
                async with session.get(url) as response:
                    response.raise_for_status()
                    with open(local_path, "wb") as f:
                        while True:
                            chunk = await response.content.read(8192)
                            if not chunk:
                                break
                            f.write(chunk)

            self._downloaded_files[url] = local_path
            logger.info(f"Successfully downloaded file to {local_path}")
            return local_path

        except Exception as e:
            logger.error(f"Error downloading file from URL: {e!s}")
            if not self.silent_errors:
                raise
            return None

    async def _validate_and_resolve_paths_async(
        self,
    ) -> list[BaseFileComponent.BaseFile]:
        """Handle URLs and local paths asynchronously."""
        resolved_files = []
        file_paths = self._file_path_as_list()

        for obj in file_paths:
            server_file_path = obj.data.get(self.SERVER_FILE_PATH_FIELDNAME)

            if not server_file_path:
                if not self.ignore_unspecified_files:
                    msg = f"Data object missing '{self.SERVER_FILE_PATH_FIELDNAME}' property."
                    if not self.silent_errors:
                        raise ValueError(msg)
                continue

            try:
                # Handle if server_file_path is a list
                paths_to_process = (
                    server_file_path
                    if isinstance(server_file_path, list)
                    else [server_file_path]
                )

                for path in paths_to_process:
                    try:
                        # Check if it's a URL
                        if isinstance(path, str) and path.startswith(
                            ("http://", "https://")
                        ):
                            local_path = await self._download_file_from_url(path)
                            if not local_path:
                                continue

                            # Create a new Data object with both the original URL and local path
                            new_data = Data(
                                data={
                                    self.SERVER_FILE_PATH_FIELDNAME: local_path,
                                    "original_url": path,
                                }
                            )

                            resolved_files.append(
                                BaseFileComponent.BaseFile(
                                    new_data,
                                    Path(local_path),
                                    delete_after_processing=True,
                                )
                            )
                        else:
                            # Handle local files
                            resolved_path = Path(self.resolve_path(str(path)))
                            if not resolved_path.exists():
                                msg = f"File not found: {path}"
                                if not self.silent_errors:
                                    raise ValueError(msg)
                                continue

                            resolved_files.append(
                                BaseFileComponent.BaseFile(
                                    obj,
                                    resolved_path,
                                    delete_after_processing=self.delete_server_file_after_processing,
                                )
                            )

                    except Exception as e:
                        logger.error(f"Error processing path {path}: {e!s}")
                        if not self.silent_errors:
                            raise
                        continue

            except Exception as e:
                logger.error(f"Error processing file paths: {e!s}")
                if not self.silent_errors:
                    raise
                continue

        return resolved_files

