            logger.debug(f"Model response: {response}")
            return response
        except Exception as e:
            logger.error(f"Error in SRF extraction: {e!s}")
            if not self.silent_errors:
                raise ValueError(f"Error in SRF extraction: {e!s}") from e
            return None

    def process_files(
        self, file_list: list[BaseFileComponent.BaseFile]
    ) -> list[BaseFileComponent.BaseFile]:
        """Process the files as required by BaseFileComponent"""
        if not file_list:
            msg = "No files to process."
            if not self.silent_errors:
                raise ValueError(msg)
            logger.warning(msg)
        return file_list

    async def process_srf(self) -> Data:
        """Process multiple images and generate SRF identification results"""
        try:
            # Use async validation that handles URLs
            files = await self._validate_and_resolve_paths_async()
            if not files:
                msg = "No valid files provided"
                logger.warning(msg)
                if not self.silent_errors:
                    raise ValueError(msg)
                return Data(value=[])  # Return empty list instead of error

            results = []
            for file in files:
                try:
                    # Read the file
                    # image_data = await self._read_file(file.path)
                    # if not image_data:
                    #     msg = f"Could not read file: {file.path}"
                    #     logger.warning(msg)
                    #     continue

                    # Extract SRF identification from the image
                    result = await self.extract_srf(file.path)
                    logger.debug(
                        f"Model result: {result}"
                    )  # Debug log to see the result

                    if result and isinstance(result, dict):
                        # Add file information to result with the new format
                        original_url = file.data[0].data.get(
                            "original_url", str(file.path)
                        )

                        # Extract the prediction value from the nested data structure
                        # Handle both possible response formats
                        if "data" in result:
                            if (
                                isinstance(result["data"], dict)
                                and "data" in result["data"]
                            ):
                                prediction = result["data"]["data"]
                            else:
                                prediction = result["data"]
                        else:
                            prediction = "unknown"

                        results.append(
                            {"file_path": original_url, "prediction": prediction}
                        )
                        logger.debug(
                            f"Processed result: {results[-1]}"
                        )  # Debug log the processed result

                    # Handle cleanup
                    if file.delete_after_processing and file.path.exists():
                        try:
                            os.remove(file.path)
                        except Exception as e:
                            logger.warning(f"Failed to delete file {file.path}: {e!s}")

                except Exception as e:
                    logger.error(f"Error processing file {file.path}: {e!s}")
                    if not self.silent_errors:
                        continue
                    logger.warning("Continuing to next file due to silent_errors=True")

            if not results:
                msg = "No successful identifications"
                logger.warning(msg)
                return Data(value=[])  # Return empty list instead of raising error

            # Return array of results directly
            logger.info(f"Successfully processed {len(results)} files")
            return Data(value=results)

        except Exception as e:
            logger.error(f"Error processing images: {e!s}")
            if not self.silent_errors:
                raise ValueError(f"Error processing images: {e!s}")
            return Data(value=[])  # Return empty list instead of error

    def __del__(self):
        """Clean up temporary files."""
        if (
            hasattr(self, "temp_dir")
            and self.temp_dir
            and os.path.exists(self.temp_dir)
        ):
            try:
