                        for key, value in doc_info.items():
                            # If key doesn't exist or current value is empty/null, use new value
                            if key not in combined_results or combined_results[key] in (
                                None,
                                "",
                                "null",
                                "Information not found",
                                "Information not",
                            ):
                                combined_results[key] = value
                                logger.debug(
                                    f"Added/Updated key {key} with value {value}"
                                )

                        # Store individual result
                        results.append(
                            {
                                "file_path": original_url,
                                "prediction": {"Doc_Info": doc_info},
                            }
                        )

                    # Handle cleanup
                    if file.delete_after_processing and file.path.exists():
                        try:
                            os.remove(file.path)
                        except Exception as e:
                            logger.warning(f"Failed to delete file {file.path}: {e!s}")

                except Exception as e:
                    logger.error(f"Error processing file {file.path}: {e!s}")
                    if not self.silent_errors:
                        continue
                    logger.warning("Continuing to next file due to silent_errors=True")

            if not results:
                msg = "No successful extractions"
                logger.warning(msg)
                return Data(value={})

            # Return both combined results and individual results
            logger.info(f"Successfully processed {len(results)} files")
            # final_result = {
            #     "combined_results": combined_results,
            #     "results": results
            # }
            logger.debug(f"Final result: {combined_results}")
            return Data(value=combined_results)

        except Exception as e:
            logger.error(f"Error processing images: {e!s}")
            if not self.silent_errors:
                raise ValueError(f"Error processing images: {e!s}")
            return Data(value={})

    def __del__(self):
        """Clean up temporary files."""
        if (
            hasattr(self, "temp_dir")
            and self.temp_dir
            and os.path.exists(self.temp_dir)
        ):
            try:
                for file_path in self._downloaded_files.values():
                    if os.path.exists(file_path):
                        os.remove(file_path)
                os.rmdir(self.temp_dir)
            except Exception as e:
                logger.error(f"Error cleaning up temporary files: {e!s}")
